	/** The Constant SUBMIT. */
	// set constant actions
	public static final String SUBMIT = "submit";
	
	/** The Constant BACK. */
	public static final String BACK = "back";

	/** The forgot password model. */
	private ForgotPassPageModel forgotPasswordModel = null;
	
	/** The forgot password panel. */
